example.synopsis:
log {
    source { file("/var/log/apache2/access.log" flags(no-parse)); };
    parser { apache-accesslog-parser(); };
    destination {
        logmatic(token("<API-KEY-AS-PROVIDED-BY-LOGMATIC.IO>")
           tag(apache)
           template("$(format-json .apache.* timestamp=${ISODATE})"));
    };
}
