section.synopsis:
Declaration:
    parser parser_name {
        json-parser(
        marker()
        prefix()
    );
};

example.synopsis:
source s_json {
    tcp(port(21514) flags(no-parse));
};

destination d_json {
    file("/tmp/test.json"
        template("$(format-json --scope dot-nv-pairs)\n"));
};

parser p_json {
    json-parser (prefix(".json."));
};

log {
    source(s_json);
    parser(p_json);
    destination(d_json);
};
