    network(ip(0.0.0.0) port(6514)
        transport("tls")
        tls( key-file("/usr/local/etc/syslog-ng/cert.d/serverkey.pem")
             cert-file("/usr/local/etc/syslog-ng/cert.d/servercert.pem")
             ca-dir("/usr/local/etc/syslog-ng/ca.d"))
    ); };

