example.synopsis:
@version: 3.2
@include "scl.conf"
    options { 
        time_reap(30);
        mark_freq(10);
        keep_hostname(yes);
        chain_hostnames(no);
        };
    source s_local { system(); internal(); };
    source s_network { syslog(transport(tcp))};

    destination d_syslog_tcp {
        syslog("192.168.1.5" transport("tcp") port(2010));
    };

    log { source(s_local); source(s_network); 
          destination(d_syslog_tcp); 
        };
